let influxResult = msg.payload;

if (Array.isArray(influxResult) && influxResult.length > 0) {
    // Extrahiere die Arbeit in Wattstunden
    let energyWh = influxResult[0]._value;

    // Umwandlung in Kilowattstunden
    let energyKWh = energyWh / 1000;

    // Daten für den InfluxDB-Out-Node vorbereiten
    msg.payload = [
        {
            measurement: "tagesenergie-Bezug",
            fields: { energie: energyKWh },
            tags: { device: "shellypro3em-EsMi_Pfauenhof" }
        }
    ];

    return msg;
} else {
    // Setze die Ausgabe auf 0, falls keine Daten gefunden wurden
    msg.payload = [
        {
            measurement: "tagesenergie-Bezug",
            fields: { energie: 0 },
            tags: { device: "shellypro3em-EsMi_Pfauenhof" }
        }
    ];

    return msg;
}

let influxResult = msg.payload;

if (Array.isArray(influxResult) && influxResult.length > 0) {
    // Extrahiere die Arbeit in Wattstunden und kehre das Vorzeichen um
    let energyWh = Math.abs(influxResult[0]._value);

    // Umwandlung in Kilowattstunden
    let energyKWh = energyWh / 1000;

    // Daten für den InfluxDB-Out-Node vorbereiten
    msg.payload = [
        {
            measurement: "tagesenergie-Einspeisung",
            fields: { energie: energyKWh },
            tags: { device: "shellypro3em-EsMi_Pfauenhof" }
        }
    ];

    return msg;
} else {
    // Setze die Ausgabe auf 0, falls keine Daten gefunden wurden
    msg.payload = [
        {
            measurement: "tagesenergie-Einspeisung",
            fields: { energie: 0 },
            tags: { device: "shellypro3em-EsMi_Pfauenhof" }
        }
    ];

    return msg;
}
